import React, { Component } from 'react';

export default class Edit extends Component {
  state = {
    name: '',
    price: '',
    imageUrl: '',
    description: '',
    shipping: ''
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  render() {
    const {
      name,
      price,
      imageUrl,
      description,
      shipping,
      errorMessage
    } = this.state;

    return (
      <form onSubmit={this.createItem}>
        <h1>Edit Trinket</h1>

        <p>{errorMessage}</p>
        <input
          type='text'
          name='name'
          placeholder='Name'
          value={name}
          onChange={this.handleChange}
        />
        <input
          type='number'
          name='price'
          placeholder='Price'
          value={price}
          onChange={this.handleChange}
        />
        <input
          type='text'
          name='imageUrl'
          placeholder='Image URL'
          value={imageUrl}
          onChange={this.handleChange}
        />
        <input
          type='description'
          name='description'
          placeholder='Description'
          value={description}
          onChange={this.handleChange}
        />
        <input
          type='shipping'
          name='shipping'
          placeholder='Shipping'
          value={shipping}
          onChange={this.handleChange}
        />

        <button type='submit'>Edit</button>
      </form>
    );
  }
}
