import React, { Component } from 'react';

export default class Create extends Component {
  state = {
    name: '',
    price: '',
    imageUrl: ''
  };

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  createItem = e => {
    e.preventDefault();
  };

  render() {
    const { name, price, imageUrl } = this.state;
    return (
      <form onSubmit={this.createItem}>
        <input
          type='text'
          name='name'
          placeholder='Name'
          value={name}
          onChange={this.handleChange}
        />
        <input
          type='number'
          name='price'
          placeholder='Price'
          value={price}
          onChange={this.handleChange}
        />
        <input
          type='text'
          name='imageURL'
          placeholder='Image URL'
          value={imageUrl}
          onChange={this.handleChange}
        />
        <input
          type='number'
          name='price'
          placeholder='Price'
          value={price}
          onChange={this.handleChange}
        />
      </form>
    );
  }
}
